package exempletext;

public class RandomEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int num = (int)(Math.random()*6)+1 ;

	
	if(num == 1) {
		System.out.println("1");
	}
	else if(num==2) {
		System.out.println("2");
	}
	else if (num==3) {
		System.out.println("3");
	}
	else if (num==4) {
		System.out.println("3");
	}
	else if (num==5) {
		System.out.println("3");
	}
	else  {
		System.out.println("6");
	}
	}

}
